contracts/advanced/APIGateway.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IStakingRewards {
    function totalStaked() external view returns (uint256);
    function rewardRate() external view returns (uint256);
}

interface ILendingProtocol {
    function totalSupplied() external view returns (uint256);
    function utilizationRate() external view returns (uint256);
    function supplyAPY() external view returns (uint256);
    function borrowAPY() external view returns (uint256);
}

interface IBridgeConnector {
    function totalBridged() external view returns (uint256);
}

interface ILRT {
    function totalStakedSGT() external view returns (uint256);
    function getExchangeRate() external view returns (uint256);
    function totalSupply() external view returns (uint256);
}

interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
}

/**
 * @title APIGateway
 * @notice On-chain data provider for analytics and monitoring
 * @dev Gas-optimized read-only functions for protocol metrics
 */
contract APIGateway is Ownable {
    struct StakingMetrics {
        uint256 totalStaked;
        uint256 totalRewards;
        uint256 averageAPY;
        uint256 activeStakers;
        uint256 rewardRate;
    }

    struct LendingMetrics {
        uint256 totalSupplied;
        uint256 totalBorrowed;
        uint256 utilizationRate;
        uint256 supplyAPY;
        uint256 borrowAPY;
    }

    struct BridgeMetrics {
        uint256 totalVolume;
        uint256 totalTransactions;
        uint256 averageBridgeTime;
    }

    struct LRTMetrics {
        uint256 totalStaked;
        uint256 exchangeRate;
        uint256 totalsSGT;
        uint256 estimatedAPY;
    }

    struct ProtocolMetrics {
        uint256 totalTVL;
        uint256 totalUsers;
        uint256 daoTreasury;
        uint256 totalTransactions;
        uint256 totalFees;
    }

    address public sgtToken;
    address public stakingRewards;
    address public lendingProtocol;
    address public bridgeConnector;
    address public lrt;
    address public daoTreasury;

    mapping(address => bool) public registeredUsers;
    uint256 public totalUsers;
    uint256 public totalTransactions;
    uint256 public totalFeesCollected;

    event ContractRegistered(string indexed contractType, address indexed contractAddress);
    event UserRegistered(address indexed user);
    event TransactionRecorded(uint256 indexed transactionId);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Register contract addresses
     * @param _sgtToken SGT token address
     * @param _stakingRewards Staking rewards address
     * @param _lendingProtocol Lending protocol address
     * @param _bridgeConnector Bridge connector address
     * @param _lrt LRT address
     * @param _daoTreasury DAO treasury address
     */
    function registerContracts(
        address _sgtToken,
        address _stakingRewards,
        address _lendingProtocol,
        address _bridgeConnector,
        address _lrt,
        address _daoTreasury
    ) external onlyOwner {
        sgtToken = _sgtToken;
        stakingRewards = _stakingRewards;
        lendingProtocol = _lendingProtocol;
        bridgeConnector = _bridgeConnector;
        lrt = _lrt;
        daoTreasury = _daoTreasury;

        emit ContractRegistered("SGT", _sgtToken);
        emit ContractRegistered("Staking", _stakingRewards);
        emit ContractRegistered("Lending", _lendingProtocol);
        emit ContractRegistered("Bridge", _bridgeConnector);
        emit ContractRegistered("LRT", _lrt);
    }

    /**
     * @notice Get total value locked across protocol
     */
    function getTotalTVL() external view returns (uint256) {
        uint256 tvl = 0;

        if (stakingRewards != address(0)) {
            tvl += IStakingRewards(stakingRewards).totalStaked();
        }

        if (lendingProtocol != address(0)) {
            tvl += ILendingProtocol(lendingProtocol).totalSupplied();
        }

        if (lrt != address(0)) {
            tvl += ILRT(lrt).totalStakedSGT();
        }

        return tvl;
    }

    /**
     * @notice Get staking metrics
     */
    function getStakingMetrics() external view returns (StakingMetrics memory) {
        if (stakingRewards == address(0)) {
            return StakingMetrics(0, 0, 0, 0, 0);
        }

        IStakingRewards staking = IStakingRewards(stakingRewards);

        return StakingMetrics({
            totalStaked: staking.totalStaked(),
            totalRewards: 0,
            averageAPY: 0,
            activeStakers: 0,
            rewardRate: staking.rewardRate()
        });
    }

    /**
     * @notice Get lending metrics
     */
    function getLendingMetrics() external view returns (LendingMetrics memory) {
        if (lendingProtocol == address(0)) {
            return LendingMetrics(0, 0, 0, 0, 0);
        }

        ILendingProtocol lending = ILendingProtocol(lendingProtocol);

        return LendingMetrics({
            totalSupplied: lending.totalSupplied(),
            totalBorrowed: 0,
            utilizationRate: lending.utilizationRate(),
            supplyAPY: lending.supplyAPY(),
            borrowAPY: lending.borrowAPY()
        });
    }

    /**
     * @notice Get bridge metrics
     */
    function getBridgeMetrics() external view returns (BridgeMetrics memory) {
        if (bridgeConnector == address(0)) {
            return BridgeMetrics(0, 0, 0);
        }

        IBridgeConnector bridge = IBridgeConnector(bridgeConnector);

        return BridgeMetrics({
            totalVolume: bridge.totalBridged(),
            totalTransactions: 0,
            averageBridgeTime: 15 minutes
        });
    }

    /**
     * @notice Get LRT metrics
     */
    function getLRTMetrics() external view returns (LRTMetrics memory) {
        if (lrt == address(0)) {
            return LRTMetrics(0, 0, 0, 0);
        }

        ILRT lrtContract = ILRT(lrt);

        return LRTMetrics({
            totalStaked: lrtContract.totalStakedSGT(),
            exchangeRate: lrtContract.getExchangeRate(),
            totalsSGT: lrtContract.totalSupply(),
            estimatedAPY: 0
        });
    }

    /**
     * @notice Get protocol-wide metrics
     */
    function getProtocolMetrics() external view returns (ProtocolMetrics memory) {
        uint256 tvl = 0;

        if (stakingRewards != address(0)) {
            tvl += IStakingRewards(stakingRewards).totalStaked();
        }

        if (lendingProtocol != address(0)) {
            tvl += ILendingProtocol(lendingProtocol).totalSupplied();
        }

        if (lrt != address(0)) {
            tvl += ILRT(lrt).totalStakedSGT();
        }

        uint256 treasury = 0;
        if (daoTreasury != address(0) && sgtToken != address(0)) {
            treasury = IERC20(sgtToken).balanceOf(daoTreasury);
        }

        return ProtocolMetrics({
            totalTVL: tvl,
            totalUsers: totalUsers,
            daoTreasury: treasury,
            totalTransactions: totalTransactions,
            totalFees: totalFeesCollected
        });
    }

    /**
     * @notice Get bridge volume
     */
    function getBridgeVolume() external view returns (uint256) {
        if (bridgeConnector == address(0)) return 0;
        return IBridgeConnector(bridgeConnector).totalBridged();
    }

    /**
     * @notice Get DAO treasury balance
     */
    function getDAOTreasury() external view returns (uint256) {
        if (daoTreasury == address(0) || sgtToken == address(0)) return 0;
        return IERC20(sgtToken).balanceOf(daoTreasury);
    }

    /**
     * @notice Get active users count
     */
    function getActiveUsers() external view returns (uint256) {
        return totalUsers;
    }

    /**
     * @notice Get SGT token supply
     */
    function getSGTSupply() external view returns (uint256) {
        if (sgtToken == address(0)) return 0;
        return IERC20(sgtToken).totalSupply();
    }

    /**
     * @notice Record user activity
     * @param user User address
     */
    function recordUser(address user) external {
        if (!registeredUsers[user]) {
            registeredUsers[user] = true;
            totalUsers++;
            emit UserRegistered(user);
        }
    }

    /**
     * @notice Record transaction
     */
    function recordTransaction() external {
        totalTransactions++;
        emit TransactionRecorded(totalTransactions);
    }

    /**
     * @notice Record fees
     * @param amount Fee amount
     */
    function recordFees(uint256 amount) external onlyOwner {
        totalFeesCollected += amount;
    }

    /**
     * @notice Get all metrics in one call (gas efficient)
     */
    function getAllMetrics() external view returns (
        uint256 tvl,
        uint256 users,
        uint256 transactions,
        uint256 fees,
        uint256 treasury
    ) {
        tvl = 0;

        if (stakingRewards != address(0)) {
            tvl += IStakingRewards(stakingRewards).totalStaked();
        }

        if (lendingProtocol != address(0)) {
            tvl += ILendingProtocol(lendingProtocol).totalSupplied();
        }

        if (lrt != address(0)) {
            tvl += ILRT(lrt).totalStakedSGT();
        }

        if (daoTreasury != address(0) && sgtToken != address(0)) {
            treasury = IERC20(sgtToken).balanceOf(daoTreasury);
        }

        return (tvl, totalUsers, totalTransactions, totalFeesCollected, treasury);
    }
}

contracts/advanced/ScrollGenBridge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ScrollGenBridge
 * @notice Cross-chain bridge for SGT tokens between Scroll and Ethereum
 * @dev Implements lock/mint/burn/release pattern for secure cross-chain transfers
 */
contract ScrollGenBridge is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    enum BridgeStatus {
        Initiated,
        Relaying,
        Completed,
        Claimed,
        Failed
    }

    struct BridgeTransaction {
        address sender;
        address recipient;
        uint256 amount;
        uint256 sourceChain;
        uint256 destinationChain;
        uint256 timestamp;
        bytes32 txHash;
        BridgeStatus status;
        uint256 fee;
    }

    IERC20 public immutable sgtToken;

    uint256 public constant SCROLL_CHAIN_ID = 534351;
    uint256 public constant ETHEREUM_CHAIN_ID = 11155111;

    uint256 public bridgeFee = 0.001 ether;
    uint256 public maxBridgeAmount = 10000 * 1e18;
    uint256 public dailyLimit = 100000 * 1e18;
    uint256 public bridgeDelay = 15 minutes;

    mapping(bytes32 => BridgeTransaction) public transactions;
    mapping(address => bytes32[]) public userTransactions;
    mapping(uint256 => uint256) public dailyVolume;
    mapping(address => bool) public relayers;

    uint256 public totalBridged;
    uint256 public totalFees;
    bool public paused;

    event BridgeInitiated(
        address indexed sender,
        address indexed recipient,
        uint256 amount,
        uint256 sourceChain,
        uint256 destinationChain,
        bytes32 indexed txHash
    );

    event BridgeRelayed(bytes32 indexed txHash, address indexed relayer);

    event BridgeCompleted(
        address indexed recipient,
        uint256 amount,
        bytes32 indexed txHash
    );

    event BridgeClaimed(
        address indexed recipient,
        uint256 amount,
        bytes32 indexed txHash
    );

    event BridgeFailed(bytes32 indexed txHash, string reason);

    event RelayerUpdated(address indexed relayer, bool status);

    event BridgePaused(bool status);

    modifier onlyRelayer() {
        require(relayers[msg.sender] || msg.sender == owner(), "Not authorized relayer");
        _;
    }

    modifier whenNotPaused() {
        require(!paused, "Bridge is paused");
        _;
    }

    constructor(address _sgtToken) Ownable(msg.sender) {
        require(_sgtToken != address(0), "Invalid SGT address");
        sgtToken = IERC20(_sgtToken);
        relayers[msg.sender] = true;
    }

    /**
     * @notice Lock SGT on source chain to bridge to destination
     * @param amount Amount of SGT to bridge
     * @param destinationChain Destination chain ID
     * @param recipient Recipient address on destination chain
     */
    function lockSGT(
        uint256 amount,
        uint256 destinationChain,
        address recipient
    ) external payable nonReentrant whenNotPaused returns (bytes32) {
        require(amount > 0 && amount <= maxBridgeAmount, "Invalid amount");
        require(msg.value >= bridgeFee, "Insufficient bridge fee");
        require(recipient != address(0), "Invalid recipient");
        require(
            destinationChain == SCROLL_CHAIN_ID || destinationChain == ETHEREUM_CHAIN_ID,
            "Invalid destination chain"
        );

        uint256 today = block.timestamp / 1 days;
        require(dailyVolume[today] + amount <= dailyLimit, "Daily limit exceeded");

        sgtToken.safeTransferFrom(msg.sender, address(this), amount);

        bytes32 txHash = keccak256(
            abi.encodePacked(msg.sender, recipient, amount, block.timestamp, block.chainid)
        );

        transactions[txHash] = BridgeTransaction({
            sender: msg.sender,
            recipient: recipient,
            amount: amount,
            sourceChain: block.chainid,
            destinationChain: destinationChain,
            timestamp: block.timestamp,
            txHash: txHash,
            status: BridgeStatus.Initiated,
            fee: msg.value
        });

        userTransactions[msg.sender].push(txHash);
        dailyVolume[today] += amount;
        totalBridged += amount;
        totalFees += msg.value;

        emit BridgeInitiated(
            msg.sender,
            recipient,
            amount,
            block.chainid,
            destinationChain,
            txHash
        );

        return txHash;
    }

    /**
     * @notice Mint bridged SGT on destination chain (relayer only)
     * @param recipient Recipient address
     * @param amount Amount to mint
     * @param sourceTxHash Transaction hash from source chain
     */
    function mintBridgedSGT(
        address recipient,
        uint256 amount,
        bytes32 sourceTxHash
    ) external onlyRelayer nonReentrant whenNotPaused {
        BridgeTransaction storage txn = transactions[sourceTxHash];

        require(txn.status == BridgeStatus.Initiated || txn.status == BridgeStatus.Relaying, "Invalid status");
        require(block.timestamp >= txn.timestamp + bridgeDelay, "Bridge delay not met");

        txn.status = BridgeStatus.Completed;

        sgtToken.safeTransfer(recipient, amount);

        emit BridgeCompleted(recipient, amount, sourceTxHash);
    }

    /**
     * @notice Release SGT on source chain after burn on destination
     * @param recipient Recipient address
     * @param amount Amount to release
     * @param burnTxHash Burn transaction hash from destination
     */
    function releaseSGT(
        address recipient,
        uint256 amount,
        bytes32 burnTxHash
    ) external onlyRelayer nonReentrant whenNotPaused {
        BridgeTransaction storage txn = transactions[burnTxHash];

        require(txn.status == BridgeStatus.Completed, "Not completed");
        require(block.timestamp >= txn.timestamp + bridgeDelay, "Delay not met");

        txn.status = BridgeStatus.Claimed;

        sgtToken.safeTransfer(recipient, amount);

        emit BridgeClaimed(recipient, amount, burnTxHash);
    }

    /**
     * @notice Update bridge transaction status (relayer only)
     * @param txHash Transaction hash
     * @param newStatus New status
     */
    function updateBridgeStatus(
        bytes32 txHash,
        BridgeStatus newStatus
    ) external onlyRelayer {
        BridgeTransaction storage txn = transactions[txHash];
        require(txn.amount > 0, "Transaction not found");

        txn.status = newStatus;

        emit BridgeRelayed(txHash, msg.sender);
    }

    /**
     * @notice Get bridge transaction details
     * @param txHash Transaction hash
     */
    function getBridgeTransaction(bytes32 txHash)
        external
        view
        returns (BridgeTransaction memory)
    {
        return transactions[txHash];
    }

    /**
     * @notice Get user's bridge transactions
     * @param user User address
     */
    function getUserTransactions(address user)
        external
        view
        returns (bytes32[] memory)
    {
        return userTransactions[user];
    }

    /**
     * @notice Get estimated bridge time
     * @param amount Bridge amount
     */
    function getEstimatedBridgeTime(uint256 amount)
        external
        view
        returns (uint256)
    {
        if (amount < 1000 * 1e18) return bridgeDelay;
        if (amount < 5000 * 1e18) return bridgeDelay + 5 minutes;
        return bridgeDelay + 10 minutes;
    }

    /**
     * @notice Calculate bridge fee
     * @param amount Bridge amount
     */
    function calculateBridgeFee(uint256 amount)
        external
        view
        returns (uint256)
    {
        if (amount < 1000 * 1e18) return bridgeFee;
        if (amount < 5000 * 1e18) return bridgeFee * 95 / 100;
        return bridgeFee * 90 / 100;
    }

    /**
     * @notice Update relayer status (owner only)
     * @param relayer Relayer address
     * @param status Authorized status
     */
    function setRelayer(address relayer, bool status) external onlyOwner {
        relayers[relayer] = status;
        emit RelayerUpdated(relayer, status);
    }

    /**
     * @notice Update bridge parameters (owner only)
     * @param newFee New bridge fee
     * @param newMaxAmount New max bridge amount
     * @param newDailyLimit New daily limit
     */
    function updateBridgeParams(
        uint256 newFee,
        uint256 newMaxAmount,
        uint256 newDailyLimit
    ) external onlyOwner {
        bridgeFee = newFee;
        maxBridgeAmount = newMaxAmount;
        dailyLimit = newDailyLimit;
    }

    /**
     * @notice Pause/unpause bridge (owner only)
     * @param _paused Pause status
     */
    function setPaused(bool _paused) external onlyOwner {
        paused = _paused;
        emit BridgePaused(_paused);
    }

    /**
     * @notice Withdraw collected fees (owner only)
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        payable(owner()).transfer(balance);
    }

    /**
     * @notice Emergency withdraw tokens (owner only)
     * @param token Token address
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        IERC20(token).safeTransfer(owner(), amount);
    }

    receive() external payable {}
}

contracts/advanced/ScrollGenDEXAggregato.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ScrollGenDEXAggregator
 * @notice DEX aggregator for optimal swap routing across multiple liquidity sources
 * @dev Simplified Uniswap V2-style router with fee governance
 */
contract ScrollGenDEXAggregator is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Pool {
        address tokenA;
        address tokenB;
        uint256 reserveA;
        uint256 reserveB;
        uint256 fee;
        bool active;
    }

    struct SwapRoute {
        address[] path;
        uint256[] poolIndices;
        uint256 expectedOutput;
        uint256 priceImpact;
    }

    mapping(bytes32 => Pool) public pools;
    bytes32[] public poolIds;

    uint256 public baseFeeRate = 30;
    uint256 public constant FEE_DENOMINATOR = 10000;
    uint256 public constant MAX_FEE = 100;

    address public governance;
    address public feeCollector;

    uint256 public totalSwapVolume;
    uint256 public totalFeesCollected;

    event PoolAdded(bytes32 indexed poolId, address tokenA, address tokenB);
    event PoolUpdated(bytes32 indexed poolId, uint256 reserveA, uint256 reserveB);
    event Swap(
        address indexed user,
        address indexed tokenIn,
        address indexed tokenOut,
        uint256 amountIn,
        uint256 amountOut,
        address[] path
    );
    event FeeRateUpdated(uint256 newRate);
    event GovernanceUpdated(address indexed newGovernance);

    modifier onlyGovernance() {
        require(msg.sender == governance || msg.sender == owner(), "Not governance");
        _;
    }

    constructor(address _feeCollector) Ownable(msg.sender) {
        require(_feeCollector != address(0), "Invalid fee collector");
        feeCollector = _feeCollector;
        governance = msg.sender;
    }

    /**
     * @notice Add liquidity pool
     * @param tokenA First token address
     * @param tokenB Second token address
     * @param reserveA Initial reserve of tokenA
     * @param reserveB Initial reserve of tokenB
     */
    function addPool(
        address tokenA,
        address tokenB,
        uint256 reserveA,
        uint256 reserveB
    ) external onlyOwner returns (bytes32) {
        require(tokenA != tokenB, "Identical tokens");
        require(tokenA != address(0) && tokenB != address(0), "Zero address");

        bytes32 poolId = keccak256(abi.encodePacked(tokenA, tokenB));

        pools[poolId] = Pool({
            tokenA: tokenA,
            tokenB: tokenB,
            reserveA: reserveA,
            reserveB: reserveB,
            fee: baseFeeRate,
            active: true
        });

        poolIds.push(poolId);

        emit PoolAdded(poolId, tokenA, tokenB);
        return poolId;
    }

    /**
     * @notice Execute token swap
     * @param amountIn Input amount
     * @param amountOutMin Minimum output amount (slippage protection)
     * @param path Swap path (token addresses)
     * @param to Recipient address
     * @param deadline Transaction deadline
     */
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external nonReentrant returns (uint256[] memory amounts) {
        require(deadline >= block.timestamp, "Expired");
        require(path.length >= 2, "Invalid path");
        require(amountIn > 0, "Zero amount");

        amounts = getAmountsOut(amountIn, path);
        require(amounts[amounts.length - 1] >= amountOutMin, "Insufficient output");

        IERC20(path[0]).safeTransferFrom(msg.sender, address(this), amountIn);

        _swap(amounts, path, to);

        totalSwapVolume += amountIn;

        emit Swap(msg.sender, path[0], path[path.length - 1], amountIn, amounts[amounts.length - 1], path);

        return amounts;
    }

    /**
     * @notice Internal swap execution
     * @param amounts Calculated swap amounts
     * @param path Swap path
     * @param to Recipient
     */
    function _swap(
        uint256[] memory amounts,
        address[] calldata path,
        address to
    ) internal {
        for (uint256 i = 0; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            uint256 amountOut = amounts[i + 1];

            bytes32 poolId = keccak256(abi.encodePacked(input, output));
            Pool storage pool = pools[poolId];

            if (!pool.active) {
                poolId = keccak256(abi.encodePacked(output, input));
                pool = pools[poolId];
            }

            require(pool.active, "Pool not active");

            uint256 feeAmount = (amountOut * pool.fee) / FEE_DENOMINATOR;
            uint256 amountAfterFee = amountOut - feeAmount;

            if (i == path.length - 2) {
                IERC20(output).safeTransfer(to, amountAfterFee);
            }

            if (feeAmount > 0) {
                IERC20(output).safeTransfer(feeCollector, feeAmount);
                totalFeesCollected += feeAmount;
            }

            _updatePoolReserves(poolId, input, output, amounts[i], amountOut);
        }
    }

    /**
     * @notice Calculate output amounts for swap path
     * @param amountIn Input amount
     * @param path Swap path
     */
    function getAmountsOut(uint256 amountIn, address[] calldata path)
        public
        view
        returns (uint256[] memory amounts)
    {
        require(path.length >= 2, "Invalid path");
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;

        for (uint256 i = 0; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(path[i], path[i + 1]);
            amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
        }
    }

    /**
     * @notice Calculate output amount for single hop
     * @param amountIn Input amount
     * @param reserveIn Input token reserve
     * @param reserveOut Output token reserve
     */
    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) public view returns (uint256 amountOut) {
        require(amountIn > 0, "Insufficient input");
        require(reserveIn > 0 && reserveOut > 0, "Insufficient liquidity");

        uint256 amountInWithFee = amountIn * (FEE_DENOMINATOR - baseFeeRate);
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = (reserveIn * FEE_DENOMINATOR) + amountInWithFee;
        amountOut = numerator / denominator;
    }

    /**
     * @notice Get pool reserves
     * @param tokenA Token A address
     * @param tokenB Token B address
     */
    function getReserves(address tokenA, address tokenB)
        public
        view
        returns (uint256 reserveA, uint256 reserveB)
    {
        bytes32 poolId = keccak256(abi.encodePacked(tokenA, tokenB));
        Pool memory pool = pools[poolId];

        if (pool.active) {
            return (pool.reserveA, pool.reserveB);
        }

        poolId = keccak256(abi.encodePacked(tokenB, tokenA));
        pool = pools[poolId];

        require(pool.active, "Pool not found");
        return (pool.reserveB, pool.reserveA);
    }

    /**
     * @notice Find best swap route
     * @param tokenIn Input token
     * @param tokenOut Output token
     * @param amountIn Input amount
     */
    function findBestRoute(
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) external view returns (SwapRoute memory) {
        address[] memory directPath = new address[](2);
        directPath[0] = tokenIn;
        directPath[1] = tokenOut;

        uint256[] memory directAmounts = _getAmountsOutMemory(amountIn, directPath);
        uint256 directOutput = directAmounts[directAmounts.length - 1];

        return SwapRoute({
            path: directPath,
            poolIndices: new uint256[](1),
            expectedOutput: directOutput,
            priceImpact: calculatePriceImpact(amountIn, directOutput)
        });
    }

    /**
     * @notice Internal helper for memory path arrays
     */
    function _getAmountsOutMemory(uint256 amountIn, address[] memory path)
        internal
        view
        returns (uint256[] memory amounts)
    {
        require(path.length >= 2, "Invalid path");
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;

        for (uint256 i = 0; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(path[i], path[i + 1]);
            amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
        }
    }

    /**
     * @notice Calculate price impact
     * @param amountIn Input amount
     * @param amountOut Output amount
     */
    function calculatePriceImpact(uint256 amountIn, uint256 amountOut)
        public
        pure
        returns (uint256)
    {
        if (amountIn == 0 || amountOut == 0) return 0;
        uint256 ratio = (amountOut * 10000) / amountIn;
        if (ratio >= 10000) return 0;
        return 10000 - ratio;
    }

    /**
     * @notice Update pool reserves after swap
     * @param poolId Pool identifier
     * @param tokenIn Input token
     * @param tokenOut Output token
     * @param amountIn Input amount
     * @param amountOut Output amount
     */
    function _updatePoolReserves(
        bytes32 poolId,
        address tokenIn,
        address tokenOut,
        uint256 amountIn,
        uint256 amountOut
    ) internal {
        Pool storage pool = pools[poolId];

        if (pool.tokenA == tokenIn) {
            pool.reserveA += amountIn;
            pool.reserveB -= amountOut;
        } else {
            pool.reserveB += amountIn;
            pool.reserveA -= amountOut;
        }

        emit PoolUpdated(poolId, pool.reserveA, pool.reserveB);
    }

    /**
     * @notice Set base fee rate (governance only)
     * @param newRate New fee rate (in basis points)
     */
    function setFeeRate(uint256 newRate) external onlyGovernance {
        require(newRate <= MAX_FEE, "Fee too high");
        baseFeeRate = newRate;
        emit FeeRateUpdated(newRate);
    }

    /**
     * @notice Update governance address
     * @param newGovernance New governance address
     */
    function setGovernance(address newGovernance) external onlyOwner {
        require(newGovernance != address(0), "Invalid address");
        governance = newGovernance;
        emit GovernanceUpdated(newGovernance);
    }

    /**
     * @notice Update pool status
     * @param poolId Pool identifier
     * @param active Active status
     */
    function setPoolStatus(bytes32 poolId, bool active) external onlyOwner {
        pools[poolId].active = active;
    }

    /**
     * @notice Get all active pools
     */
    function getActivePools() external view returns (bytes32[] memory) {
        uint256 count = 0;
        for (uint256 i = 0; i < poolIds.length; i++) {
            if (pools[poolIds[i]].active) count++;
        }

        bytes32[] memory activePools = new bytes32[](count);
        uint256 index = 0;
        for (uint256 i = 0; i < poolIds.length; i++) {
            if (pools[poolIds[i]].active) {
                activePools[index] = poolIds[i];
                index++;
            }
        }

        return activePools;
    }
}

contracts/advanced/ScrollGenLRT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title ScrollGenLRT (Liquid Restaked Token)
 * @notice Enables liquid staking derivatives - users restake SGT for yield-bearing sSGT
 * @dev sSGT represents staked SGT with accumulated yield, fully transferable
 */
contract ScrollGenLRT is ERC20, Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct RedemptionRequest {
        address user;
        uint256 sSGTAmount;
        uint256 sgtAmount;
        uint256 requestTime;
        bool processed;
    }

    IERC20 public immutable sgtToken;

    uint256 public totalStakedSGT;
    uint256 public totalYieldAccrued;
    uint256 public redemptionDelay = 7 days;
    uint256 public minRestakeAmount = 100 * 1e18;

    mapping(uint256 => RedemptionRequest) public redemptionRequests;
    uint256 public nextRequestId = 1;
    mapping(address => uint256[]) public userRequests;

    uint256 public constant PRECISION = 1e18;
    uint256 public yieldRate = 1e17;
    uint256 public lastYieldUpdate;

    event Restaked(address indexed user, uint256 sgtAmount, uint256 sSGTMinted);
    event RedemptionRequested(address indexed user, uint256 requestId, uint256 sSGTAmount);
    event RedemptionClaimed(address indexed user, uint256 requestId, uint256 sgtAmount);
    event YieldAccrued(uint256 amount, uint256 newExchangeRate);
    event YieldRateUpdated(uint256 newRate);

    constructor(address _sgtToken) ERC20("Staked ScrollGen Token", "sSGT") Ownable(msg.sender) {
        require(_sgtToken != address(0), "Invalid SGT address");
        sgtToken = IERC20(_sgtToken);
        lastYieldUpdate = block.timestamp;
    }

    /**
     * @notice Restake SGT to receive sSGT
     * @param amount Amount of SGT to restake
     * @return sSGTMinted Amount of sSGT minted
     */
    function restake(uint256 amount) external nonReentrant returns (uint256 sSGTMinted) {
        require(amount >= minRestakeAmount, "Amount below minimum");

        _accrueYield();

        sgtToken.safeTransferFrom(msg.sender, address(this), amount);

        sSGTMinted = (amount * PRECISION) / getExchangeRate();

        _mint(msg.sender, sSGTMinted);

        totalStakedSGT += amount;

        emit Restaked(msg.sender, amount, sSGTMinted);

        return sSGTMinted;
    }

    /**
     * @notice Request redemption of sSGT for SGT
     * @param sSGTAmount Amount of sSGT to redeem
     * @return requestId Redemption request ID
     */
    function requestRedeem(uint256 sSGTAmount) external nonReentrant returns (uint256 requestId) {
        require(sSGTAmount > 0, "Zero amount");
        require(balanceOf(msg.sender) >= sSGTAmount, "Insufficient balance");

        _accrueYield();

        uint256 sgtAmount = (sSGTAmount * getExchangeRate()) / PRECISION;

        _burn(msg.sender, sSGTAmount);

        requestId = nextRequestId++;
        redemptionRequests[requestId] = RedemptionRequest({
            user: msg.sender,
            sSGTAmount: sSGTAmount,
            sgtAmount: sgtAmount,
            requestTime: block.timestamp,
            processed: false
        });

        userRequests[msg.sender].push(requestId);

        emit RedemptionRequested(msg.sender, requestId, sSGTAmount);

        return requestId;
    }

    /**
     * @notice Claim redeemed SGT after delay period
     * @param requestId Redemption request ID
     */
    function claimRedeem(uint256 requestId) external nonReentrant {
        RedemptionRequest storage request = redemptionRequests[requestId];

        require(request.user == msg.sender, "Not request owner");
        require(!request.processed, "Already processed");
        require(
            block.timestamp >= request.requestTime + redemptionDelay,
            "Delay not met"
        );

        request.processed = true;

        sgtToken.safeTransfer(msg.sender, request.sgtAmount);

        totalStakedSGT -= request.sgtAmount;

        emit RedemptionClaimed(msg.sender, requestId, request.sgtAmount);
    }

    /**
     * @notice Get current exchange rate (sSGT to SGT)
     * @return Exchange rate scaled by PRECISION
     */
    function getExchangeRate() public view returns (uint256) {
        uint256 totalsSGT = totalSupply();
        if (totalsSGT == 0) return PRECISION;

        uint256 totalValue = totalStakedSGT + totalYieldAccrued;
        return (totalValue * PRECISION) / totalsSGT;
    }

    /**
     * @notice Get total staked value in SGT
     */
    function getTotalStakedValue() external view returns (uint256) {
        return totalStakedSGT + totalYieldAccrued;
    }

    /**
     * @notice Convert sSGT amount to SGT
     * @param sSGTAmount Amount of sSGT
     */
    function convertToSGT(uint256 sSGTAmount) external view returns (uint256) {
        return (sSGTAmount * getExchangeRate()) / PRECISION;
    }

    /**
     * @notice Convert SGT amount to sSGT
     * @param sgtAmount Amount of SGT
     */
    function convertTosSGT(uint256 sgtAmount) external view returns (uint256) {
        return (sgtAmount * PRECISION) / getExchangeRate();
    }

    /**
     * @notice Get user's redemption requests
     * @param user User address
     */
    function getUserRequests(address user) external view returns (uint256[] memory) {
        return userRequests[user];
    }

    /**
     * @notice Check if redemption is claimable
     * @param requestId Request ID
     */
    function isClaimable(uint256 requestId) external view returns (bool) {
        RedemptionRequest memory request = redemptionRequests[requestId];
        return !request.processed &&
               block.timestamp >= request.requestTime + redemptionDelay;
    }

    /**
     * @notice Get time until claimable
     * @param requestId Request ID
     */
    function getTimeUntilClaimable(uint256 requestId) external view returns (uint256) {
        RedemptionRequest memory request = redemptionRequests[requestId];
        if (request.processed) return 0;

        uint256 claimTime = request.requestTime + redemptionDelay;
        if (block.timestamp >= claimTime) return 0;

        return claimTime - block.timestamp;
    }

    /**
     * @notice Get estimated APY
     */
    function getEstimatedAPY() external view returns (uint256) {
        if (totalStakedSGT == 0) return 0;
        return (yieldRate * 365 days * 100) / PRECISION;
    }

    /**
     * @notice Accrue yield to increase exchange rate
     */
    function _accrueYield() internal {
        uint256 timeElapsed = block.timestamp - lastYieldUpdate;
        if (timeElapsed == 0 || totalStakedSGT == 0) return;

        uint256 yield = (totalStakedSGT * yieldRate * timeElapsed) / (365 days * PRECISION);

        totalYieldAccrued += yield;
        lastYieldUpdate = block.timestamp;

        emit YieldAccrued(yield, getExchangeRate());
    }

    /**
     * @notice Manually accrue yield (anyone can call)
     */
    function accrueYield() external {
        _accrueYield();
    }

    /**
     * @notice Set yield rate (owner only)
     * @param newRate New yield rate
     */
    function setYieldRate(uint256 newRate) external onlyOwner {
        _accrueYield();
        yieldRate = newRate;
        emit YieldRateUpdated(newRate);
    }

    /**
     * @notice Set redemption delay (owner only)
     * @param newDelay New delay in seconds
     */
    function setRedemptionDelay(uint256 newDelay) external onlyOwner {
        require(newDelay >= 1 days && newDelay <= 30 days, "Invalid delay");
        redemptionDelay = newDelay;
    }

    /**
     * @notice Set minimum restake amount (owner only)
     * @param newMin New minimum amount
     */
    function setMinRestakeAmount(uint256 newMin) external onlyOwner {
        minRestakeAmount = newMin;
    }

    /**
     * @notice Deposit yield from external source (owner only)
     * @param amount Yield amount
     */
    function depositYield(uint256 amount) external onlyOwner {
        sgtToken.safeTransferFrom(msg.sender, address(this), amount);
        totalYieldAccrued += amount;
        emit YieldAccrued(amount, getExchangeRate());
    }
}

