contracts/BridgeConnector.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title BridgeConnector
 * @notice Connector for Scroll bridge operations and transaction tracking
 * @dev Tracks cross-chain transactions and provides status information
 */
contract BridgeConnector is Ownable, ReentrancyGuard {
    enum BridgeStatus {
        Pending,
        Confirmed,
        Batched,
        Finalized,
        ReadyToClaim,
        Completed,
        Failed
    }

    enum BridgeDirection {
        Deposit,
        Withdrawal
    }

    struct BridgeTransaction {
        address user;
        uint256 amount;
        address token;
        uint256 timestamp;
        bytes32 l1TxHash;
        bytes32 l2TxHash;
        BridgeDirection direction;
        BridgeStatus status;
        uint256 claimableAt;
    }

    mapping(bytes32 => BridgeTransaction) public transactions;
    mapping(address => bytes32[]) public userTransactions;

    address public scrollMessenger;
    uint256 public constant WITHDRAWAL_DELAY = 7 days;
    uint256 public depositFee;
    uint256 public withdrawalFee;

    event DepositInitiated(
        address indexed user,
        uint256 amount,
        address token,
        bytes32 indexed txHash
    );

    event WithdrawalInitiated(
        address indexed user,
        uint256 amount,
        address token,
        bytes32 indexed txHash
    );

    event TransactionStatusUpdated(
        bytes32 indexed txHash,
        BridgeStatus oldStatus,
        BridgeStatus newStatus
    );

    event WithdrawalClaimed(
        address indexed user,
        bytes32 indexed txHash,
        uint256 amount
    );

    event FeeUpdated(uint256 depositFee, uint256 withdrawalFee);

    constructor(address _scrollMessenger) Ownable(msg.sender) {
        require(_scrollMessenger != address(0), "Invalid messenger");
        scrollMessenger = _scrollMessenger;
        depositFee = 0;
        withdrawalFee = 0;
    }

    /**
     * @notice Initiate a deposit from L1 to L2
     * @param amount Amount to deposit
     * @param token Token address
     * @param l1TxHash Transaction hash from L1
     */
    function initiateDeposit(
        uint256 amount,
        address token,
        bytes32 l1TxHash
    ) external payable nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(token != address(0), "Invalid token");
        require(msg.value >= depositFee, "Insufficient fee");

        BridgeTransaction storage txn = transactions[l1TxHash];
        require(txn.user == address(0), "Transaction already exists");

        txn.user = msg.sender;
        txn.amount = amount;
        txn.token = token;
        txn.timestamp = block.timestamp;
        txn.l1TxHash = l1TxHash;
        txn.direction = BridgeDirection.Deposit;
        txn.status = BridgeStatus.Pending;

        userTransactions[msg.sender].push(l1TxHash);

        emit DepositInitiated(msg.sender, amount, token, l1TxHash);
    }

    /**
     * @notice Initiate a withdrawal from L2 to L1
     * @param amount Amount to withdraw
     * @param token Token address
     * @param l2TxHash Transaction hash from L2
     */
    function initiateWithdrawal(
        uint256 amount,
        address token,
        bytes32 l2TxHash
    ) external payable nonReentrant {
        require(amount > 0, "Amount must be > 0");
        require(token != address(0), "Invalid token");
        require(msg.value >= withdrawalFee, "Insufficient fee");

        BridgeTransaction storage txn = transactions[l2TxHash];
        require(txn.user == address(0), "Transaction already exists");

        txn.user = msg.sender;
        txn.amount = amount;
        txn.token = token;
        txn.timestamp = block.timestamp;
        txn.l2TxHash = l2TxHash;
        txn.direction = BridgeDirection.Withdrawal;
        txn.status = BridgeStatus.Pending;
        txn.claimableAt = block.timestamp + WITHDRAWAL_DELAY;

        userTransactions[msg.sender].push(l2TxHash);

        emit WithdrawalInitiated(msg.sender, amount, token, l2TxHash);
    }

    /**
     * @notice Update transaction status
     * @param txHash Transaction hash
     * @param newStatus New status
     */
    function updateTransactionStatus(
        bytes32 txHash,
        BridgeStatus newStatus
    ) external onlyOwner {
        BridgeTransaction storage txn = transactions[txHash];
        require(txn.user != address(0), "Transaction not found");

        BridgeStatus oldStatus = txn.status;
        txn.status = newStatus;

        emit TransactionStatusUpdated(txHash, oldStatus, newStatus);
    }

    /**
     * @notice Claim completed withdrawal
     * @param txHash Transaction hash
     */
    function claimWithdrawal(bytes32 txHash) external nonReentrant {
        BridgeTransaction storage txn = transactions[txHash];
        require(txn.user == msg.sender, "Not transaction owner");
        require(txn.direction == BridgeDirection.Withdrawal, "Not a withdrawal");
        require(txn.status == BridgeStatus.ReadyToClaim, "Not ready to claim");
        require(block.timestamp >= txn.claimableAt, "Still in challenge period");

        txn.status = BridgeStatus.Completed;

        emit WithdrawalClaimed(msg.sender, txHash, txn.amount);
    }

    /**
     * @notice Get transaction details
     * @param txHash Transaction hash
     */
    function getTransaction(bytes32 txHash) external view returns (BridgeTransaction memory) {
        return transactions[txHash];
    }

    /**
     * @notice Get all user transactions
     * @param user User address
     */
    function getUserTransactions(address user) external view returns (bytes32[] memory) {
        return userTransactions[user];
    }

    /**
     * @notice Get transaction status
     * @param txHash Transaction hash
     */
    function getTransactionStatus(bytes32 txHash) external view returns (BridgeStatus) {
        return transactions[txHash].status;
    }

    /**
     * @notice Check if withdrawal is claimable
     * @param txHash Transaction hash
     */
    function isClaimable(bytes32 txHash) external view returns (bool) {
        BridgeTransaction memory txn = transactions[txHash];
        return txn.status == BridgeStatus.ReadyToClaim &&
               block.timestamp >= txn.claimableAt;
    }

    /**
     * @notice Get time remaining until claimable
     * @param txHash Transaction hash
     */
    function getTimeUntilClaimable(bytes32 txHash) external view returns (uint256) {
        BridgeTransaction memory txn = transactions[txHash];
        if (block.timestamp >= txn.claimableAt) return 0;
        return txn.claimableAt - block.timestamp;
    }

    /**
     * @notice Update bridge fees
     * @param _depositFee New deposit fee
     * @param _withdrawalFee New withdrawal fee
     */
    function updateFees(uint256 _depositFee, uint256 _withdrawalFee) external onlyOwner {
        depositFee = _depositFee;
        withdrawalFee = _withdrawalFee;
        emit FeeUpdated(_depositFee, _withdrawalFee);
    }

    /**
     * @notice Update scroll messenger address
     * @param _scrollMessenger New messenger address
     */
    function updateScrollMessenger(address _scrollMessenger) external onlyOwner {
        require(_scrollMessenger != address(0), "Invalid messenger");
        scrollMessenger = _scrollMessenger;
    }

    /**
     * @notice Withdraw collected fees
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        payable(owner()).transfer(balance);
    }

    /**
     * @notice Receive ETH
     */
    receive() external payable {}
}

contracts/LendingProtocol.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title LendingProtocol
 * @notice Simple lending market for supplying and borrowing assets
 * @dev Implements collateralized lending with variable interest rates
 */
contract LendingProtocol is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Supply {
        uint256 amount;
        uint256 timestamp;
        uint256 interestEarned;
        uint256 lastUpdateTime;
    }

    struct Borrow {
        uint256 collateral;
        uint256 borrowed;
        address asset;
        uint256 timestamp;
        uint256 interestAccrued;
        uint256 lastUpdateTime;
    }

    IERC20 public immutable collateralToken;

    mapping(address => Supply) public supplies;
    mapping(address => Borrow[]) public borrows;
    mapping(address => bool) public supportedAssets;
    mapping(address => uint256) public totalBorrowedByAsset;

    uint256 public totalSupplied;
    uint256 public totalCollateral;

    uint256 public constant LTV_RATIO = 66;
    uint256 public constant LIQUIDATION_THRESHOLD = 75;
    uint256 public constant LIQUIDATION_PENALTY = 10;
    uint256 public constant PRECISION = 100;

    uint256 public constant BASE_RATE = 2e16;
    uint256 public constant OPTIMAL_UTILIZATION = 80;
    uint256 public constant RATE_SLOPE_1 = 8e16;
    uint256 public constant RATE_SLOPE_2 = 40e16;

    event Supplied(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event Borrowed(address indexed user, address indexed asset, uint256 amount, uint256 borrowIndex);
    event Repaid(address indexed user, uint256 borrowIndex, uint256 amount);
    event Liquidated(address indexed user, uint256 borrowIndex, address indexed liquidator);
    event AssetAdded(address indexed asset);
    event AssetRemoved(address indexed asset);

    constructor(address _collateralToken) Ownable(msg.sender) {
        require(_collateralToken != address(0), "Invalid collateral token");
        collateralToken = IERC20(_collateralToken);
    }

    /**
     * @notice Supply collateral tokens to earn interest
     * @param amount Amount to supply
     */
    function supply(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be > 0");

        updateSupplyInterest(msg.sender);

        collateralToken.safeTransferFrom(msg.sender, address(this), amount);

        Supply storage userSupply = supplies[msg.sender];
        userSupply.amount += amount;
        userSupply.timestamp = block.timestamp;
        userSupply.lastUpdateTime = block.timestamp;

        totalSupplied += amount;

        emit Supplied(msg.sender, amount);
    }

    /**
     * @notice Withdraw supplied tokens
     * @param amount Amount to withdraw
     */
    function withdraw(uint256 amount) external nonReentrant {
        Supply storage userSupply = supplies[msg.sender];
        require(userSupply.amount >= amount, "Insufficient supply");

        updateSupplyInterest(msg.sender);

        uint256 availableLiquidity = collateralToken.balanceOf(address(this)) - totalCollateral;
        require(availableLiquidity >= amount, "Insufficient liquidity");

        userSupply.amount -= amount;
        totalSupplied -= amount;

        collateralToken.safeTransfer(msg.sender, amount);

        emit Withdrawn(msg.sender, amount);
    }

    /**
     * @notice Borrow assets against collateral
     * @param asset Asset to borrow
     * @param amount Amount to borrow
     */
    function borrow(address asset, uint256 amount) external nonReentrant {
        require(supportedAssets[asset], "Asset not supported");
        require(amount > 0, "Amount must be > 0");

        Supply storage userSupply = supplies[msg.sender];
        require(userSupply.amount > 0, "No collateral supplied");

        uint256 borrowCapacity = (userSupply.amount * LTV_RATIO) / PRECISION;
        uint256 currentBorrowed = getTotalBorrowed(msg.sender);

        require(currentBorrowed + amount <= borrowCapacity, "Exceeds borrow capacity");

        borrows[msg.sender].push(Borrow({
            collateral: userSupply.amount,
            borrowed: amount,
            asset: asset,
            timestamp: block.timestamp,
            interestAccrued: 0,
            lastUpdateTime: block.timestamp
        }));

        totalBorrowedByAsset[asset] += amount;
        totalCollateral += userSupply.amount;

        IERC20(asset).safeTransfer(msg.sender, amount);

        emit Borrowed(msg.sender, asset, amount, borrows[msg.sender].length - 1);
    }

    /**
     * @notice Repay borrowed amount
     * @param borrowIndex Index of the borrow to repay
     * @param amount Amount to repay
     */
    function repay(uint256 borrowIndex, uint256 amount) external nonReentrant {
        require(borrowIndex < borrows[msg.sender].length, "Invalid borrow index");

        Borrow storage userBorrow = borrows[msg.sender][borrowIndex];
        require(userBorrow.borrowed > 0, "Borrow already repaid");

        updateBorrowInterest(msg.sender, borrowIndex);

        uint256 totalOwed = userBorrow.borrowed + userBorrow.interestAccrued;
        require(amount <= totalOwed, "Amount exceeds debt");

        IERC20(userBorrow.asset).safeTransferFrom(msg.sender, address(this), amount);

        if (amount >= totalOwed) {
            totalBorrowedByAsset[userBorrow.asset] -= userBorrow.borrowed;
            totalCollateral -= userBorrow.collateral;
            userBorrow.borrowed = 0;
            userBorrow.interestAccrued = 0;
        } else {
            if (amount > userBorrow.interestAccrued) {
                uint256 principalRepay = amount - userBorrow.interestAccrued;
                userBorrow.borrowed -= principalRepay;
                userBorrow.interestAccrued = 0;
                totalBorrowedByAsset[userBorrow.asset] -= principalRepay;
            } else {
                userBorrow.interestAccrued -= amount;
            }
        }

        emit Repaid(msg.sender, borrowIndex, amount);
    }

    /**
     * @notice Liquidate unhealthy position
     * @param user User to liquidate
     * @param borrowIndex Borrow index to liquidate
     */
    function liquidate(address user, uint256 borrowIndex) external nonReentrant {
        require(borrowIndex < borrows[user].length, "Invalid borrow index");

        Borrow storage userBorrow = borrows[user][borrowIndex];
        require(userBorrow.borrowed > 0, "Position already closed");

        updateBorrowInterest(user, borrowIndex);

        uint256 healthFactor = calculateHealthFactor(user, borrowIndex);
        require(healthFactor < PRECISION, "Position is healthy");

        uint256 totalDebt = userBorrow.borrowed + userBorrow.interestAccrued;
        uint256 collateralToSeize = userBorrow.collateral;
        uint256 penalty = (collateralToSeize * LIQUIDATION_PENALTY) / PRECISION;

        IERC20(userBorrow.asset).safeTransferFrom(msg.sender, address(this), totalDebt);

        collateralToken.safeTransfer(msg.sender, collateralToSeize - penalty);
        collateralToken.safeTransfer(owner(), penalty);

        totalBorrowedByAsset[userBorrow.asset] -= userBorrow.borrowed;
        totalCollateral -= userBorrow.collateral;

        userBorrow.borrowed = 0;
        userBorrow.collateral = 0;

        emit Liquidated(user, borrowIndex, msg.sender);
    }

    /**
     * @notice Calculate health factor for a borrow
     */
    function calculateHealthFactor(address user, uint256 borrowIndex) public view returns (uint256) {
        if (borrowIndex >= borrows[user].length) return 0;

        Borrow memory userBorrow = borrows[user][borrowIndex];
        if (userBorrow.borrowed == 0) return type(uint256).max;

        uint256 collateralValue = (userBorrow.collateral * LIQUIDATION_THRESHOLD) / PRECISION;
        uint256 borrowValue = userBorrow.borrowed + userBorrow.interestAccrued;

        if (borrowValue == 0) return type(uint256).max;

        return (collateralValue * PRECISION) / borrowValue;
    }

    /**
     * @notice Get total borrowed amount for a user
     */
    function getTotalBorrowed(address user) public view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 0; i < borrows[user].length; i++) {
            total += borrows[user][i].borrowed + borrows[user][i].interestAccrued;
        }
        return total;
    }

    /**
     * @notice Calculate utilization rate
     */
    function utilizationRate() public view returns (uint256) {
        if (totalSupplied == 0) return 0;
        uint256 totalBorrowed = getTotalBorrowedAll();
        return (totalBorrowed * PRECISION) / totalSupplied;
    }

    /**
     * @notice Calculate supply APY
     */
    function supplyAPY() public view returns (uint256) {
        uint256 utilization = utilizationRate();
        uint256 borrowApy = borrowAPY();
        return (borrowApy * utilization) / PRECISION;
    }

    /**
     * @notice Calculate borrow APY
     */
    function borrowAPY() public view returns (uint256) {
        uint256 utilization = utilizationRate();

        if (utilization <= OPTIMAL_UTILIZATION) {
            return BASE_RATE + (utilization * RATE_SLOPE_1) / OPTIMAL_UTILIZATION;
        } else {
            uint256 excessUtilization = utilization - OPTIMAL_UTILIZATION;
            return BASE_RATE + RATE_SLOPE_1 +
                (excessUtilization * RATE_SLOPE_2) / (PRECISION - OPTIMAL_UTILIZATION);
        }
    }

    /**
     * @notice Get all borrows for a user
     */
    function getUserBorrows(address user) external view returns (Borrow[] memory) {
        return borrows[user];
    }

    /**
     * @notice Add supported asset
     */
    function addSupportedAsset(address asset) external onlyOwner {
        require(asset != address(0), "Invalid asset");
        supportedAssets[asset] = true;
        emit AssetAdded(asset);
    }

    /**
     * @notice Remove supported asset
     */
    function removeSupportedAsset(address asset) external onlyOwner {
        supportedAssets[asset] = false;
        emit AssetRemoved(asset);
    }

    /**
     * @notice Update supply interest
     */
    function updateSupplyInterest(address user) internal {
        Supply storage userSupply = supplies[user];
        if (userSupply.amount == 0) return;

        uint256 timeElapsed = block.timestamp - userSupply.lastUpdateTime;
        uint256 apy = supplyAPY();
        uint256 interest = (userSupply.amount * apy * timeElapsed) / (365 days * 1e18);

        userSupply.interestEarned += interest;
        userSupply.lastUpdateTime = block.timestamp;
    }

    /**
     * @notice Update borrow interest
     */
    function updateBorrowInterest(address user, uint256 borrowIndex) internal {
        Borrow storage userBorrow = borrows[user][borrowIndex];
        if (userBorrow.borrowed == 0) return;

        uint256 timeElapsed = block.timestamp - userBorrow.lastUpdateTime;
        uint256 apy = borrowAPY();
        uint256 interest = (userBorrow.borrowed * apy * timeElapsed) / (365 days * 1e18);

        userBorrow.interestAccrued += interest;
        userBorrow.lastUpdateTime = block.timestamp;
    }

    /**
     * @notice Get total borrowed across all assets
     */
    function getTotalBorrowedAll() internal view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 0; i < borrows[msg.sender].length; i++) {
            total += borrows[msg.sender][i].borrowed;
        }
        return total;
    }
}

contracts/StakingRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title StakingRewards
 * @notice Time-locked staking with reward multipliers for SGT token
 * @dev Implements flexible and time-locked staking pools with auto-compound
 */
contract StakingRewards is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    struct Stake {
        uint256 amount;
        uint256 startTime;
        uint256 lockDuration;
        uint256 rewardDebt;
        bool autoCompound;
        uint8 multiplier;
    }

    IERC20 public immutable stakingToken;
    IERC20 public immutable rewardToken;

    uint256 public rewardRate;
    uint256 public totalStaked;
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;

    mapping(address => Stake[]) public stakes;
    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;

    uint256 public constant PRECISION = 1e18;
    uint256 public constant MIN_STAKE = 100 * 1e18;

    event Staked(address indexed user, uint256 amount, uint256 lockDuration, uint256 stakeIndex);
    event Unstaked(address indexed user, uint256 amount, uint256 stakeIndex);
    event RewardsClaimed(address indexed user, uint256 amount);
    event Compounded(address indexed user, uint256 amount, uint256 stakeIndex);
    event RewardRateUpdated(uint256 newRate);

    constructor(
        address _stakingToken,
        address _rewardToken,
        uint256 _initialRewardRate
    ) Ownable(msg.sender) {
        require(_stakingToken != address(0), "Invalid staking token");
        require(_rewardToken != address(0), "Invalid reward token");

        stakingToken = IERC20(_stakingToken);
        rewardToken = IERC20(_rewardToken);
        rewardRate = _initialRewardRate;
        lastUpdateTime = block.timestamp;
    }

    /**
     * @notice Stake tokens with optional lock duration
     * @param amount Amount to stake
     * @param lockDuration Lock duration in seconds (0 for flexible)
     * @param autoCompound Enable auto-compounding
     */
    function stake(
        uint256 amount,
        uint256 lockDuration,
        bool autoCompound
    ) external nonReentrant updateReward(msg.sender) {
        require(amount >= MIN_STAKE, "Amount below minimum");
        require(
            lockDuration == 0 ||
            lockDuration == 30 days ||
            lockDuration == 90 days ||
            lockDuration == 180 days ||
            lockDuration == 365 days,
            "Invalid lock duration"
        );

        uint8 multiplier = getMultiplier(lockDuration);

        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        stakes[msg.sender].push(Stake({
            amount: amount,
            startTime: block.timestamp,
            lockDuration: lockDuration,
            rewardDebt: 0,
            autoCompound: autoCompound,
            multiplier: multiplier
        }));

        totalStaked += amount;

        emit Staked(msg.sender, amount, lockDuration, stakes[msg.sender].length - 1);
    }

    /**
     * @notice Unstake tokens from a specific stake
     * @param stakeIndex Index of the stake to unstake
     */
    function unstake(uint256 stakeIndex) external nonReentrant updateReward(msg.sender) {
        require(stakeIndex < stakes[msg.sender].length, "Invalid stake index");

        Stake storage userStake = stakes[msg.sender][stakeIndex];
        require(userStake.amount > 0, "Stake already withdrawn");
        require(
            block.timestamp >= userStake.startTime + userStake.lockDuration,
            "Stake still locked"
        );

        uint256 amount = userStake.amount;
        uint256 reward = calculateStakeReward(msg.sender, stakeIndex);

        userStake.amount = 0;
        totalStaked -= amount;

        stakingToken.safeTransfer(msg.sender, amount);

        if (reward > 0) {
            rewards[msg.sender] += reward;
        }

        emit Unstaked(msg.sender, amount, stakeIndex);
    }

    /**
     * @notice Claim accumulated rewards
     */
    function claimRewards() external nonReentrant updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        require(reward > 0, "No rewards to claim");

        rewards[msg.sender] = 0;
        rewardToken.safeTransfer(msg.sender, reward);

        emit RewardsClaimed(msg.sender, reward);
    }

    /**
     * @notice Compound rewards into a specific stake
     * @param stakeIndex Index of the stake to compound into
     */
    function compound(uint256 stakeIndex) external nonReentrant updateReward(msg.sender) {
        require(stakeIndex < stakes[msg.sender].length, "Invalid stake index");

        Stake storage userStake = stakes[msg.sender][stakeIndex];
        require(userStake.amount > 0, "Stake withdrawn");
        require(userStake.autoCompound, "Auto-compound not enabled");

        uint256 reward = calculateStakeReward(msg.sender, stakeIndex);
        require(reward > 0, "No rewards to compound");

        userStake.amount += reward;
        totalStaked += reward;

        emit Compounded(msg.sender, reward, stakeIndex);
    }

    /**
     * @notice Calculate rewards for a specific stake
     */
    function calculateStakeReward(address user, uint256 stakeIndex) public view returns (uint256) {
        if (stakeIndex >= stakes[user].length) return 0;

        Stake memory userStake = stakes[user][stakeIndex];
        if (userStake.amount == 0) return 0;

        uint256 timeStaked = block.timestamp - userStake.startTime;
        uint256 baseReward = (userStake.amount * rewardRate * timeStaked) / (365 days * PRECISION);
        uint256 multipliedReward = (baseReward * userStake.multiplier) / 10;

        return multipliedReward;
    }

    /**
     * @notice Calculate total earned rewards for a user
     */
    function earned(address user) public view returns (uint256) {
        uint256 total = rewards[user];

        for (uint256 i = 0; i < stakes[user].length; i++) {
            total += calculateStakeReward(user, i);
        }

        return total;
    }

    /**
     * @notice Get multiplier based on lock duration
     */
    function getMultiplier(uint256 lockDuration) public pure returns (uint8) {
        if (lockDuration == 0) return 10;        // 1x
        if (lockDuration == 30 days) return 15;  // 1.5x
        if (lockDuration == 90 days) return 20;  // 2x
        if (lockDuration == 180 days) return 30; // 3x
        if (lockDuration == 365 days) return 50; // 5x
        return 10;
    }

    /**
     * @notice Get all stakes for a user
     */
    function getUserStakes(address user) external view returns (Stake[] memory) {
        return stakes[user];
    }

    /**
     * @notice Get total user staked amount
     */
    function getUserTotalStaked(address user) external view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 0; i < stakes[user].length; i++) {
            total += stakes[user][i].amount;
        }
        return total;
    }

    /**
     * @notice Update reward rate (owner only)
     */
    function setRewardRate(uint256 newRate) external onlyOwner updateReward(address(0)) {
        rewardRate = newRate;
        emit RewardRateUpdated(newRate);
    }

    /**
     * @notice Emergency withdraw rewards (owner only)
     */
    function emergencyWithdrawRewards(uint256 amount) external onlyOwner {
        rewardToken.safeTransfer(owner(), amount);
    }

    /**
     * @notice Update rewards modifier
     */
    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;

        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /**
     * @notice Calculate reward per token
     */
    function rewardPerToken() public view returns (uint256) {
        if (totalStaked == 0) {
            return rewardPerTokenStored;
        }

        return rewardPerTokenStored +
            (((block.timestamp - lastUpdateTime) * rewardRate * PRECISION) / totalStaked);
    }
}

contracts/ZKVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title ZKVerifier
 * @notice Verifies zero-knowledge proofs for private operations
 * @dev Integrates with Groth16 verifier contracts for different proof types
 */
contract ZKVerifier is Ownable {
    enum ProofType {
        StakeProof,
        NFTOwnership,
        VoteProof,
        TransferProof
    }

    struct Proof {
        uint256[2] a;
        uint256[2][2] b;
        uint256[2] c;
    }

    mapping(bytes32 => bool) public nullifiersUsed;
    mapping(ProofType => address) public verifierContracts;
    mapping(address => uint256) public proofCount;

    event ProofVerified(
        address indexed user,
        ProofType indexed proofType,
        bytes32 nullifier,
        uint256 timestamp
    );

    event VerifierUpdated(
        ProofType indexed proofType,
        address indexed verifier
    );

    event NullifierUsed(bytes32 indexed nullifier);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Verify stake amount proof
     * @param proof The ZK proof components
     * @param publicInputs Public inputs for verification
     * @param nullifier Unique nullifier to prevent reuse
     */
    function verifyStakeProof(
        Proof calldata proof,
        uint256[] calldata publicInputs,
        bytes32 nullifier
    ) external returns (bool) {
        require(!nullifiersUsed[nullifier], "Nullifier already used");
        require(publicInputs.length > 0, "Missing public inputs");

        address verifier = verifierContracts[ProofType.StakeProof];
        require(verifier != address(0), "Verifier not set");

        bool isValid = _callVerifier(verifier, proof, publicInputs);
        require(isValid, "Invalid proof");

        nullifiersUsed[nullifier] = true;
        proofCount[msg.sender]++;

        emit ProofVerified(msg.sender, ProofType.StakeProof, nullifier, block.timestamp);
        emit NullifierUsed(nullifier);

        return true;
    }

    /**
     * @notice Verify NFT ownership proof
     * @param proof The ZK proof components
     * @param publicInputs Public inputs for verification
     * @param nullifier Unique nullifier
     */
    function verifyNFTOwnership(
        Proof calldata proof,
        uint256[] calldata publicInputs,
        bytes32 nullifier
    ) external returns (bool) {
        require(!nullifiersUsed[nullifier], "Nullifier already used");
        require(publicInputs.length > 0, "Missing public inputs");

        address verifier = verifierContracts[ProofType.NFTOwnership];
        require(verifier != address(0), "Verifier not set");

        bool isValid = _callVerifier(verifier, proof, publicInputs);
        require(isValid, "Invalid proof");

        nullifiersUsed[nullifier] = true;
        proofCount[msg.sender]++;

        emit ProofVerified(msg.sender, ProofType.NFTOwnership, nullifier, block.timestamp);
        emit NullifierUsed(nullifier);

        return true;
    }

    /**
     * @notice Verify vote proof for governance
     * @param proof The ZK proof components
     * @param publicInputs Public inputs for verification
     * @param nullifier Unique vote nullifier
     */
    function verifyVoteProof(
        Proof calldata proof,
        uint256[] calldata publicInputs,
        bytes32 nullifier
    ) external returns (bool) {
        require(!nullifiersUsed[nullifier], "Vote already cast");
        require(publicInputs.length > 0, "Missing public inputs");

        address verifier = verifierContracts[ProofType.VoteProof];
        require(verifier != address(0), "Verifier not set");

        bool isValid = _callVerifier(verifier, proof, publicInputs);
        require(isValid, "Invalid proof");

        nullifiersUsed[nullifier] = true;
        proofCount[msg.sender]++;

        emit ProofVerified(msg.sender, ProofType.VoteProof, nullifier, block.timestamp);
        emit NullifierUsed(nullifier);

        return true;
    }

    /**
     * @notice Verify transfer proof
     * @param proof The ZK proof components
     * @param publicInputs Public inputs for verification
     * @param nullifier Unique transfer nullifier
     */
    function verifyTransferProof(
        Proof calldata proof,
        uint256[] calldata publicInputs,
        bytes32 nullifier
    ) external returns (bool) {
        require(!nullifiersUsed[nullifier], "Nullifier already used");
        require(publicInputs.length > 0, "Missing public inputs");

        address verifier = verifierContracts[ProofType.TransferProof];
        require(verifier != address(0), "Verifier not set");

        bool isValid = _callVerifier(verifier, proof, publicInputs);
        require(isValid, "Invalid proof");

        nullifiersUsed[nullifier] = true;
        proofCount[msg.sender]++;

        emit ProofVerified(msg.sender, ProofType.TransferProof, nullifier, block.timestamp);
        emit NullifierUsed(nullifier);

        return true;
    }

    /**
     * @notice Check if nullifier has been used
     * @param nullifier Nullifier to check
     */
    function isNullifierUsed(bytes32 nullifier) external view returns (bool) {
        return nullifiersUsed[nullifier];
    }

    /**
     * @notice Get user's total proof count
     * @param user User address
     */
    function getUserProofCount(address user) external view returns (uint256) {
        return proofCount[user];
    }

    /**
     * @notice Set verifier contract for a proof type
     * @param proofType Type of proof
     * @param verifier Verifier contract address
     */
    function setVerifier(
        ProofType proofType,
        address verifier
    ) external onlyOwner {
        require(verifier != address(0), "Invalid verifier address");
        verifierContracts[proofType] = verifier;
        emit VerifierUpdated(proofType, verifier);
    }

    /**
     * @notice Internal function to call verifier contract
     * @param verifier Verifier contract address
     * @param proof Proof components
     * @param publicInputs Public inputs
     */
    function _callVerifier(
        address verifier,
        Proof calldata proof,
        uint256[] calldata publicInputs
    ) internal view returns (bool) {
        bytes memory callData = abi.encodeWithSignature(
            "verifyProof(uint256[2],uint256[2][2],uint256[2],uint256[])",
            proof.a,
            proof.b,
            proof.c,
            publicInputs
        );

        (bool success, bytes memory result) = verifier.staticcall(callData);

        if (!success) return false;
        return abi.decode(result, (bool));
    }

    /**
     * @notice Batch verify multiple proofs
     * @param proofs Array of proofs
     * @param publicInputsArray Array of public inputs
     * @param nullifiers Array of nullifiers
     * @param proofTypes Array of proof types
     */
    function batchVerify(
        Proof[] calldata proofs,
        uint256[][] calldata publicInputsArray,
        bytes32[] calldata nullifiers,
        ProofType[] calldata proofTypes
    ) external returns (bool[] memory) {
        require(
            proofs.length == publicInputsArray.length &&
            proofs.length == nullifiers.length &&
            proofs.length == proofTypes.length,
            "Array length mismatch"
        );

        bool[] memory results = new bool[](proofs.length);

        for (uint256 i = 0; i < proofs.length; i++) {
            if (nullifiersUsed[nullifiers[i]]) {
                results[i] = false;
                continue;
            }

            address verifier = verifierContracts[proofTypes[i]];
            if (verifier == address(0)) {
                results[i] = false;
                continue;
            }

            bool isValid = _callVerifier(verifier, proofs[i], publicInputsArray[i]);

            if (isValid) {
                nullifiersUsed[nullifiers[i]] = true;
                proofCount[msg.sender]++;
                emit ProofVerified(msg.sender, proofTypes[i], nullifiers[i], block.timestamp);
            }

            results[i] = isValid;
        }

        return results;
    }
}

